                        <h4>Índice de Receitas</h4>
                        <div class="cards-container">
                            <?php
                            if (!$conn) {
                                die("Erro na conexão com o banco de dados: " . mysqli_connect_error());
                            }

                            // Consulta SQL para recuperar todos os cortes de carne
                            $sql_cortes = "SELECT id_corte, nome_corte FROM corte";
                            $result_cortes = mysqli_query($conn, $sql_cortes);

                            // Verifica se encontrou cortes
                            if (mysqli_num_rows($result_cortes) > 0) {
                                $counter = 0;
                                // Loop pelos cortes
                                while ($corte = mysqli_fetch_assoc($result_cortes)) {
                                    $id_corte = $corte['id_corte'];
                                    $nome_corte = $corte['nome_corte'];

                                    // Consulta SQL para recuperar as três primeiras receitas para o corte atual
                                    $sql_receitas = "SELECT id_rec, nome_rec FROM receita WHERE id_corte = $id_corte LIMIT 3";
                                    $result_receitas = mysqli_query($conn, $sql_receitas);

                                    // Adiciona classe 'hidden' se contador for maior ou igual a 4
                                    $hidden_class = $counter >= 4 ? 'hidden' : '';

                                    // Exibe o card para o corte atual
                                    echo "<div class='card $hidden_class'>";
                                    echo "<div class='card-title'>$nome_corte</div>";

                                    // Verifica se encontrou receitas
                                    if (mysqli_num_rows($result_receitas) > 0) {
                                        while ($receita = mysqli_fetch_assoc($result_receitas)) {
                                            $id_rec = $receita['id_rec'];
                                            $nome_rec = $receita['nome_rec'];
                                            echo "<div class='receita'>";
                                            echo "<div class='receita-titulo'><a href='receita.php?id=$id_rec'>" . $nome_rec . "</a></div>";
                                            echo "</div>";
                                        }
                                    } else {
                                        echo "<div class='receita'>Nenhuma receita encontrada para este corte.</div>";
                                    }

                                    echo "</div>";

                                    $counter++;
                                }
                            } else {
                                echo "Nenhum corte de carne encontrado.";
                            }

                            mysqli_close($conn);
                            ?>
